const express=require("express"),app=express();app.get("/server",(request,response)=>{response.setHeader("Access-Control-Allow-Origin","*"),response.send("hello ajax")}),app.post("/server",(request,response)=>{response.setHeader("Access-Control-Allow-Origin","*"),response.setHeader("Access-Control-Allow-Header","*"),response.send("hello ajax post")}),app.listen(8e3,()=>{console.log("服务启动，8000端口监听中")});